            ),
            339 => 
            array (
                'id' => 22340,
                'capitulo_id' => 871,
                'pericopa_id' => 1519,
                'num_versiculo' => 16,
                'contenido' => 'Efraín fue herido; se secó su raíz y no dará más fruto; aunque engendren, yo mataré el preciado fruto de su vientre.',
                'referencia' => 'Oseas 9:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
            ),
            340 => 
            array (
                'id' => 22341,
                'capitulo_id' => 871,
                'pericopa_id' => 1519,
                'num_versiculo' => 17,
                'contenido' => 'Mi Dios los desechará, porque ellos no le escucharon; y andarán errantes entre las naciones.',
                'referencia' => 'Oseas 9:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
