            344 => 
            array (
                'id' => 22345,
                'capitulo_id' => 872,
                'pericopa_id' => 1520,
                'num_versiculo' => 4,
                'contenido' => 'Han hablado palabras, jurando en vano al hacer convenio; por tanto, el juicio florecerá como hierba venenosa en los surcos del campo.',
                'referencia' => 'Oseas 10:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:26',
                'updated_at' => '2024-03-12 15:25:26',
