            359 => 
            array (
                'id' => 22360,
                'capitulo_id' => 873,
                'pericopa_id' => 1521,
                'num_versiculo' => 4,
                'contenido' => 'Con cuerdas humanas los atraje, con cuerdas de amor; y fui para ellos como los que alzan el yugo de sobre su cerviz, y puse delante de ellos la comida.',
                'referencia' => 'Oseas 11:4',
