            39 => 
            array (
                'id' => 22540,
                'capitulo_id' => 884,
                'pericopa_id' => 1542,
                'num_versiculo' => 2,
                'contenido' => 'Cayó la virginal Israel; no podrá levantarse ya más; fue dejada sobre su tierra, y no hay quien la levante.',
                'referencia' => 'Amós 5:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
