            40 => 
            array (
                'id' => 22541,
                'capitulo_id' => 884,
                'pericopa_id' => 1542,
                'num_versiculo' => 3,
                'contenido' => 'Porque así ha dicho Jehová el Señor: La ciudad que salga con mil volverá con cien, y la que salga con cien volverá con diez, en la casa de Israel.',
                'referencia' => 'Amós 5:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
            ),
            41 => 
            array (
                'id' => 22542,
                'capitulo_id' => 884,
                'pericopa_id' => 1542,
                'num_versiculo' => 4,
                'contenido' => 'Pero así dice Jehová a la casa de Israel: Buscadme y viviréis;',
                'referencia' => 'Amós 5:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
