            44 => 
            array (
                'id' => 22545,
                'capitulo_id' => 884,
                'pericopa_id' => 1542,
                'num_versiculo' => 7,
                'contenido' => 'Los que convertís en ajenjo el juicio y echáis por tierra la justicia,',
                'referencia' => 'Amós 5:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
