            array (
                'id' => 22555,
                'capitulo_id' => 884,
                'pericopa_id' => 1542,
                'num_versiculo' => 17,
                'contenido' => 'Y en todas las viñas habrá llanto, porque pasaré por en medio de ti, dice Jehová.',
