            344 => 
            array (
                'id' => 22845,
                'capitulo_id' => 903,
                'pericopa_id' => 1578,
                'num_versiculo' => 16,
                'contenido' => 'Multiplicaste tus mercaderes más que las estrellas del cielo; el pulgón hizo presa y voló.',
                'referencia' => 'Nahúm 3:16',
                'imagen' => '',
                'pie_imagen' => '',
