            ),
            422 => 
            array (
                'id' => 22923,
                'capitulo_id' => 908,
                'pericopa_id' => 1586,
                'num_versiculo' => 1,
                'contenido' => 'Congregaos y reuníos, oh nación sin pudor,',
                'referencia' => 'Sofonías 2:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
