            423 => 
            array (
                'id' => 22924,
                'capitulo_id' => 908,
                'pericopa_id' => 1586,
                'num_versiculo' => 2,
                'contenido' => 'antes que tenga efecto el decreto y el día se pase como el tamo, antes que venga sobre vosotros el furor de la ira de Jehová, antes que el día de la ira de Jehová venga sobre vosotros.',
                'referencia' => 'Sofonías 2:2',
                'imagen' => '',
