            39 => 
            array (
                'id' => 23040,
                'capitulo_id' => 915,
                'pericopa_id' => 1604,
                'num_versiculo' => 1,
                'contenido' => 'Y volvió el ángel que hablaba conmigo y me despertó, como a un hombre que es despertado de su sueño.',
                'referencia' => 'Zacarías 4:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
