            45 => 
            array (
                'id' => 23046,
                'capitulo_id' => 915,
                'pericopa_id' => 1604,
                'num_versiculo' => 7,
                'contenido' => '¿Quién eres tú, oh gran monte? Delante de Zorobabel serás reducido a llanura; él sacará la piedra principal con aclamaciones de: ¡Gracia, gracia a ella!',
                'referencia' => 'Zacarías 4:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:27',
                'updated_at' => '2024-03-12 15:25:27',
            ),
            46 => 
            array (
                'id' => 23047,
                'capitulo_id' => 915,
                'pericopa_id' => 1604,
                'num_versiculo' => 8,
                'contenido' => 'Y vino la palabra de Jehová a mí, diciendo:',
