            array (
                'id' => 23098,
                'capitulo_id' => 919,
                'pericopa_id' => 1611,
                'num_versiculo' => 5,
                'contenido' => 'Y las calles de la ciudad estarán llenas de muchachos y muchachas que jugarán en ellas.',
