            97 => 
            array (
                'id' => 23598,
                'capitulo_id' => 940,
                'pericopa_id' => 1687,
                'num_versiculo' => 22,
                'contenido' => 'Por tanto os digo que en el día del juicio será más tolerable el castigo para Tiro y para Sidón que para vosotras.',
                'referencia' => 'Mateo 11:22',
                'imagen' => '',
                'pie_imagen' => '',
