            array (
                'id' => 23811,
                'capitulo_id' => 945,
                'pericopa_id' => 1720,
                'num_versiculo' => 22,
                'contenido' => 'Y Pedro, tomándolo aparte, comenzó a reprenderle, diciendo: Señor, ten compasión de ti mismo. ¡En ninguna manera esto te acontezca!',
