            417 => 
            array (
                'id' => 23918,
                'capitulo_id' => 949,
                'pericopa_id' => 1736,
                'num_versiculo' => 9,
                'contenido' => 'Y cuando vinieron los que habían ido cerca de la hora undécima, recibieron cada uno un denario.',
                'referencia' => 'Mateo 20:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:28',
                'updated_at' => '2024-03-12 15:25:28',
