            419 => 
            array (
                'id' => 23920,
                'capitulo_id' => 949,
                'pericopa_id' => 1736,
                'num_versiculo' => 11,
                'contenido' => 'Y tomándolo, murmuraban contra el padre de familia,',
                'referencia' => 'Mateo 20:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:28',
                'updated_at' => '2024-03-12 15:25:28',
