            420 => 
            array (
                'id' => 23921,
                'capitulo_id' => 949,
                'pericopa_id' => 1736,
                'num_versiculo' => 12,
                'contenido' => 'diciendo: Estos postreros han trabajado una sola hora, y los has hecho iguales a nosotros, que hemos llevado la carga y el calor del día.',
                'referencia' => 'Mateo 20:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:28',
                'updated_at' => '2024-03-12 15:25:28',
