            array (
                'id' => 23937,
                'capitulo_id' => 949,
                'pericopa_id' => 1738,
                'num_versiculo' => 28,
                'contenido' => 'así como el Hijo del Hombre no vino para ser servido, sino para servir y para dar su vida en rescate por muchos.',
