                'capitulo_id' => 950,
                'pericopa_id' => 1745,
                'num_versiculo' => 35,
                'contenido' => 'Pero los labradores, tomando a los siervos, a uno golpearon, y a otro mataron y a otro apedrearon.',
                'referencia' => 'Mateo 21:35',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:28',
                'updated_at' => '2024-03-12 15:25:28',
