                'capitulo_id' => 950,
                'pericopa_id' => 1745,
                'num_versiculo' => 36,
                'contenido' => 'Envió de nuevo otros siervos, más que los primeros, e hicieron con ellos de la misma manera.',
                'referencia' => 'Mateo 21:36',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:28',
                'updated_at' => '2024-03-12 15:25:28',
