            444 => 
            array (
                'id' => 24445,
                'capitulo_id' => 961,
                'pericopa_id' => 1830,
                'num_versiculo' => 5,
                'contenido' => 'Y otra parte cayó en pedregales, donde no había mucha tierra; y brotó pronto, porque la tierra no era profunda.',
                'referencia' => 'Marcos 4:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:29',
                'updated_at' => '2024-03-12 15:25:29',
