            445 => 
            array (
                'id' => 24446,
                'capitulo_id' => 961,
                'pericopa_id' => 1830,
                'num_versiculo' => 6,
                'contenido' => 'Pero cuando salió el sol, se quemó; y por cuanto no tenía raíz, se secó.',
                'referencia' => 'Marcos 4:6',
