            446 => 
            array (
                'id' => 24447,
                'capitulo_id' => 961,
                'pericopa_id' => 1830,
                'num_versiculo' => 7,
                'contenido' => 'Y otra parte cayó entre espinos; y crecieron los espinos y la ahogaron, y no dio fruto.',
                'referencia' => 'Marcos 4:7',
                'imagen' => '',
