            array (
                'id' => 24452,
                'capitulo_id' => 961,
                'pericopa_id' => 1831,
                'num_versiculo' => 12,
                'contenido' => 'para que viendo, vean y no perciban; y oyendo, oigan y no entiendan; no sea que se conviertan y les sean perdonados los pecados.',
