            456 => 
            array (
                'id' => 24457,
                'capitulo_id' => 961,
                'pericopa_id' => 1832,
                'num_versiculo' => 17,
                'contenido' => 'pero no tienen raíz en sí, sino que son temporales; y cuando viene la tribulación o la persecución por causa de la palabra, en seguida tropiezan.',
                'referencia' => 'Marcos 4:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:29',
                'updated_at' => '2024-03-12 15:25:29',
