            array (
                'id' => 24458,
                'capitulo_id' => 961,
                'pericopa_id' => 1832,
                'num_versiculo' => 18,
                'contenido' => 'Y éstos son los que son sembrados entre espinos, los que oyen la palabra,',
                'referencia' => 'Marcos 4:18',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:29',
                'updated_at' => '2024-03-12 15:25:29',
