            272 => 
            array (
                'id' => 24773,
                'capitulo_id' => 968,
                'pericopa_id' => 1875,
                'num_versiculo' => 16,
                'contenido' => 'y no consentía que nadie atravesara el templo llevando utensilio alguno.',
