            315 => 
            array (
                'id' => 25316,
                'capitulo_id' => 980,
                'pericopa_id' => 1967,
                'num_versiculo' => 4,
                'contenido' => 'Y acercándose ellos a Jesús, le rogaron con diligencia, diciéndole: Es digno de que le concedas esto,',
                'referencia' => 'Lucas 7:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:29',
                'updated_at' => '2024-03-12 15:25:29',
