            array (
                'id' => 26361,
                'capitulo_id' => 1002,
                'pericopa_id' => 2116,
                'num_versiculo' => 34,
                'contenido' => 'Pero yo no recibo testimonio de hombre alguno; mas digo esto para que vosotros seáis salvos.',
