            array (
                'id' => 26585,
                'capitulo_id' => 1006,
                'pericopa_id' => 2136,
                'num_versiculo' => 28,
                'contenido' => 'Entonces le insultaron y dijeron: Tú eres su discípulo, pero nosotros somos discípulos de Moisés.',
                'referencia' => 'Juan 9:28',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
            ),
            85 => 
            array (
                'id' => 26586,
                'capitulo_id' => 1006,
                'pericopa_id' => 2136,
                'num_versiculo' => 29,
                'contenido' => 'Nosotros sabemos que habló Dios a Moisés, pero éste, no sabemos de dónde es.',
                'referencia' => 'Juan 9:29',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
