            86 => 
            array (
                'id' => 26587,
                'capitulo_id' => 1006,
                'pericopa_id' => 2136,
                'num_versiculo' => 30,
                'contenido' => 'Respondió aquel hombre y les dijo: Pues esto es lo maravilloso, que vosotros no sepáis de dónde es, y sin embargo a mí me abrió los ojos.',
                'referencia' => 'Juan 9:30',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
