            92 => 
            array (
                'id' => 26593,
                'capitulo_id' => 1006,
                'pericopa_id' => 2137,
                'num_versiculo' => 36,
                'contenido' => 'Respondió él y le dijo: ¿Quién es, Señor, para que crea en él?',
                'referencia' => 'Juan 9:36',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
            ),
            93 => 
            array (
                'id' => 26594,
                'capitulo_id' => 1006,
                'pericopa_id' => 2137,
                'num_versiculo' => 37,
                'contenido' => 'Y le dijo Jesús: Ya le has visto, y el que habla contigo, él es.',
                'referencia' => 'Juan 9:37',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
            ),
            94 => 
            array (
                'id' => 26595,
                'capitulo_id' => 1006,
                'pericopa_id' => 2137,
                'num_versiculo' => 38,
                'contenido' => 'Y él dijo: Creo, Señor; y le adoró.',
