            392 => 
            array (
                'id' => 26893,
                'capitulo_id' => 1014,
                'pericopa_id' => 2166,
                'num_versiculo' => 17,
                'contenido' => 'Santifícalos en tu verdad; tu palabra es la verdad.',
                'referencia' => 'Juan 17:17',
