            array (
                'id' => 27200,
                'capitulo_id' => 1023,
                'pericopa_id' => 2197,
                'num_versiculo' => 23,
                'contenido' => 'diciendo: Por cierto, la cárcel hemos hallado cerrada con toda seguridad y los guardias que estaban delante de las puertas; pero cuando abrimos, a nadie hallamos dentro.',
                'referencia' => 'Hechos 5:23',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:31',
                'updated_at' => '2024-03-12 15:25:31',
            ),
