            array (
                'id' => 27613,
                'capitulo_id' => 1034,
                'pericopa_id' => 2225,
                'num_versiculo' => 12,
                'contenido' => 'y de allí a Filipos, que es la ciudad principal de esa parte de Macedonia y una colonia; y estuvimos en aquella ciudad algunos días.',
                'referencia' => 'Hechos 16:12',
