            124 => 
            array (
                'id' => 27626,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 25,
                'contenido' => 'Pero a medianoche, orando Pablo y Silas, cantaban himnos a Dios; y los que estaban presos los oían.',
                'referencia' => 'Hechos 16:25',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
