            127 => 
            array (
                'id' => 27629,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 28,
                'contenido' => 'Pero Pablo clamó a gran voz, diciendo: No te hagas ningún mal, porque todos estamos aquí.',
                'referencia' => 'Hechos 16:28',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
