            128 => 
            array (
                'id' => 27630,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 29,
                'contenido' => 'Él entonces, pidiendo luz, entró precipitadamente, y temblando, se postró a los pies de Pablo y de Silas;',
                'referencia' => 'Hechos 16:29',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
            ),
            129 => 
            array (
                'id' => 27631,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 30,
                'contenido' => 'y los sacó fuera y les dijo: Señores, ¿qué debo hacer para ser salvo?',
                'referencia' => 'Hechos 16:30',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
            ),
            130 => 
            array (
                'id' => 27632,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 31,
