                'id' => 27635,
                'capitulo_id' => 1034,
                'pericopa_id' => 4917,
                'num_versiculo' => 34,
                'contenido' => 'Y llevándolos a su casa, les puso la mesa; y se regocijó de que con toda su casa había creído en Dios.',
                'referencia' => 'Hechos 16:34',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
