                'capitulo_id' => 1034,
                'pericopa_id' => 4918,
                'num_versiculo' => 36,
                'contenido' => 'Y el carcelero hizo saber estas palabras a Pablo: Los magistrados han enviado a decir que se os suelte; así que ahora salid e id en paz.',
                'referencia' => 'Hechos 16:36',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
