            142 => 
            array (
                'id' => 27644,
                'capitulo_id' => 1035,
                'pericopa_id' => 2226,
                'num_versiculo' => 3,
                'contenido' => 'declarando y exponiendo que era necesario que el Cristo padeciese y resucitase de entre los muertos; y decía: Jesús, a quien yo os anuncio, es el Cristo.',
                'referencia' => 'Hechos 17:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
