            309 => 
            array (
                'id' => 27811,
                'capitulo_id' => 1039,
                'pericopa_id' => 2239,
                'num_versiculo' => 29,
                'contenido' => 'Porque antes habían visto con él en la ciudad a Trófimo, el efesio, al que pensaban que Pablo había metido en el templo.',
                'referencia' => 'Hechos 21:29',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
