            array (
                'id' => 27834,
                'capitulo_id' => 1040,
                'pericopa_id' => 2242,
                'num_versiculo' => 12,
                'contenido' => 'Entonces uno llamado Ananías, varón piadoso conforme a la ley, que tenía buen testimonio de todos los judíos que allí moraban,',
                'referencia' => 'Hechos 22:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
