            333 => 
            array (
                'id' => 27835,
                'capitulo_id' => 1040,
                'pericopa_id' => 2242,
                'num_versiculo' => 13,
                'contenido' => 'vino a mí y, acercándose, me dijo: Hermano Saulo, recibe de nuevo la vista. Y yo en aquella misma hora recobré la vista y le miré.',
                'referencia' => 'Hechos 22:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
