                'id' => 27837,
                'capitulo_id' => 1040,
                'pericopa_id' => 2242,
                'num_versiculo' => 15,
                'contenido' => 'Porque serás testigo suyo a todos los hombres de lo que has visto y oído.',
                'referencia' => 'Hechos 22:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
