            array (
                'id' => 27945,
                'capitulo_id' => 1044,
                'pericopa_id' => 2253,
                'num_versiculo' => 4,
                'contenido' => 'Mi vida, pues, desde mi juventud, la cual desde el principio pasé en mi nación, en Jerusalén, la conocen todos los judíos,',
                'referencia' => 'Hechos 26:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
