            444 => 
            array (
                'id' => 27946,
                'capitulo_id' => 1044,
                'pericopa_id' => 2253,
                'num_versiculo' => 5,
                'contenido' => 'quienes también saben que yo desde el principio, si quieren testificarlo, conforme a la más rigurosa secta de nuestra religión, viví como fariseo.',
                'referencia' => 'Hechos 26:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
            ),
            445 => 
            array (
                'id' => 27947,
                'capitulo_id' => 1044,
                'pericopa_id' => 2253,
                'num_versiculo' => 6,
                'contenido' => 'Y ahora, por la esperanza de la promesa que hizo Dios a nuestros padres, soy llamado a juicio;',
                'referencia' => 'Hechos 26:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
