            array (
                'id' => 27953,
                'capitulo_id' => 1044,
                'pericopa_id' => 2255,
                'num_versiculo' => 12,
                'contenido' => 'Ocupado en esto, iba yo a Damasco con la autoridad y la comisión de los principales sacerdotes,',
                'referencia' => 'Hechos 26:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
