            455 => 
            array (
                'id' => 27957,
                'capitulo_id' => 1044,
                'pericopa_id' => 2255,
                'num_versiculo' => 16,
                'contenido' => 'Pero levántate y ponte sobre tus pies; porque para esto me he aparecido a ti, para ponerte como ministro y testigo de las cosas que has visto y de aquellas en que me apareceré a ti,',
                'referencia' => 'Hechos 26:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
            ),
            456 => 
            array (
                'id' => 27958,
                'capitulo_id' => 1044,
                'pericopa_id' => 2255,
                'num_versiculo' => 17,
                'contenido' => 'librándote del pueblo y de los gentiles, a quienes ahora te envío,',
                'referencia' => 'Hechos 26:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:32',
                'updated_at' => '2024-03-12 15:25:32',
